(() => {
  // <stdin>
  var nav = document.querySelector(".c-nav");
  var navSlide = function() {
    const burger = document.querySelector(".c-nav__burger");
    const navDrawer = document.querySelector(".c-nav__links");
    const navLinks = document.querySelectorAll(".c-nav__links li");
    burger.addEventListener("click", function() {
      navDrawer.classList.toggle("nav-open");
      navLinks.forEach((link, index) => {
        if (link.style.animation) {
          link.style.animation = "";
        } else {
          link.style.animation = `navLinkFade 0.5s ease forwards ${index / 7 + 0.3}s`;
        }
      });
      burger.classList.toggle("js-toggled");
    });
    window.onscroll = () => {
      this.scrollY > 20 ? nav.classList.add("js-sticky") : nav.classList.remove("js-sticky");
    };
  };
  (function($) {
    "use strict";
    const first_section = $(".p-section_first");
    const navHeight = nav.offsetHeight;
    function srcTo(el) {
      $("html, body").animate({
        scrollTop: el.offset().top - navHeight
      }, 1e3);
    }
    $("#header-arrow").click(function() {
      srcTo(first_section);
    });
  })(jQuery);
  var runFunctions = function() {
    navSlide();
  };
  runFunctions();
})();
